SELECT t.sellerid, t.sellername, t.commission, (SELECT SUM(money) FROM t_bill WHERE sellerid = t.sellerid ) AS money, t.xmer_uid AS uid, t.saas_channel FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 3 AND t1.status IN (1, 2) AND t.`xmer_uid` IN
#{item}
UNION ALL SELECT t.sellerid, t.sellername, t.commission, (SELECT SUM(money) FROM t_bill WHERE sellerid = t.sellerid ) AS money, t.parent_xmer_uid AS uid, t.saas_channel FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 4 AND t1.status IN (1, 2) AND t.`parent_xmer_uid` IN
#{item}

SELECT t.sellerid, t.sellername, t.saas_channel, SUM(t.money) AS sellerTotalProfit, t1.fullname, t1.phoneid, t.status, t.signUid AS uid From ( SELECT t.sellerid, t.sellername, t.money, t.xmer_uid AS signUid, t1.status, t.saas_channel FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 3 AND t1.status IN (1, 2) AND t.`xmer_uid` = #{uid,jdbcType=INTEGER}
UNION ALL SELECT t.sellerid, t.sellername, t.money, t.xmer_uid AS signUid, t1.status, t.saas_channel FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 4 AND t1.status IN (1, 2) AND t.`parent_xmer_uid` = #{uid,jdbcType=INTEGER}
) t LEFT JOIN t_seller t1 ON t.sellerid = t1.sellerid GROUP BY t.sellerid;

SELECT t.* FROM ( SELECT t.bid AS orderNo, t.sellerid, t.sellername, t.commission, t.money, t.xmer_uid AS vkeUid, t.saas_channel AS saasChannel, t.baseagio AS discount, t.sdate FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 3 AND t1.status = 1
UNION ALL SELECT t.bid AS orderNo, t.sellerid, t.sellername, t.commission, t.money, t.parent_xmer_uid AS vkeUid, t.saas_channel AS saasChannel, t.baseagio AS discount, t.sdate FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 4 AND t1.status = 1
) t
1 = 1
AND t.vkeUid = #{vkeUid}
AND t.sdate > #{sdateStart}
AND t.sdate < #{sdateEnd}
ORDER BY sdate DESC
LIMIT ${(page-1)*limit},#{limit}

SELECT COUNT(1) FROM ( SELECT t.sellerid, t.sellername, t.commission, t.money, t.xmer_uid AS vkeUid, t.saas_channel AS saasChannel, t.sdate FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 3 AND t1.status = 1
UNION ALL SELECT t.sellerid, t.sellername, t.commission, t.money, t.parent_xmer_uid AS vkeUid, t.saas_channel AS saasChannel, t.sdate FROM t_bill t INNER JOIN t_saas_sold_order t1 ON t.sellerid = t1.sellerid
1 = 1 AND t.STATUS > 0 AND t.saas_channel = 4 AND t1.status = 1
) t
1 = 1
AND t.vkeUid = #{vkeUid}
AND t.sdate > #{sdateStart}
AND t.sdate < #{sdateEnd}

SELECT COUNT(1) AS vkeCount FROM ( SELECT IFNULL(t.xmer_uid, t.parent_xmer_uid) AS vkeUid FROM t_bill t
1 = 1 AND t.saas_channel IN (3, 4)
AND ((t.saas_channel = 3 AND t.xmer_uid = #{vkeUid} ) OR (t.saas_channel = 4 AND t.parent_xmer_uid = #{vkeUid} ) )
AND t.sdate > #{sdateStart}
AND t.sdate < #{sdateEnd}
GROUP BY vkeUid ) t

SELECT COUNT(1) AS sellerCount FROM ( SELECT Count(1) FROM t_bill t
1 = 1 AND t.saas_channel IN (3, 4)
AND ((t.saas_channel = 3 AND t.xmer_uid = #{vkeUid} ) OR (t.saas_channel = 4 AND t.parent_xmer_uid = #{vkeUid} ) )
AND t.sdate > #{sdateStart}
AND t.sdate < #{sdateEnd}
GROUP BY t.sellerid ) t
