mapper.select:
SELECT pid, codeId, standard, pname, goodsName, classa, secondary, price, discount,brand_id, batching, weight, suttle, quality, isAdditives, packing, production, pstatus, crafts, sellType, deal, place, province, city, stored, store, sales, choice, cname, address, licenseId, tel, rdate, udate, dstatus, sort, supplierId, breviary,salesInfo,isIntegral, purchasePrice,integral,saleType,cash,allowRefund,deliveryType,expWeight,expTid,deliveryCity,notDeliveryCity,saleCity,notSaleCity, goodsSerial,barcode ,tft1.name classaStr,tft2.name secondaryStr,ta1.title provinceStr,ta2.title cityStr, tp.supplierName,tp.contacts,tp.supplierPhone,tp.supplierAddress,tpt.expTitle,brand.brand_name as brandName FROM t_product_info tinfo LEFT JOIN (SELECT id,NAME FROM t_fresh_type) tft1 ON tft1.id = tinfo.classa LEFT JOIN (SELECT id,NAME FROM t_fresh_type) tft2 ON tft2.id = tinfo.secondary LEFT JOIN (SELECT area_id,title FROM t_area) ta1 ON ta1.area_id = tinfo.province LEFT JOIN (SELECT area_id,title FROM t_area ) ta2 ON ta2.area_id = tinfo.city LEFT JOIN (SELECT supplierId AS spId,supplierName,contacts,phone AS supplierPhone,address AS supplierAddress FROM t_supplier WHERE `status` = '0') tp ON tp.spId = tinfo.supplierId LEFT JOIN (SELECT title AS expTitle,tid FROM t_postage_template) tpt ON tpt.tid = tinfo.expTid LEFT JOIN (SELECT id,fid FROM t_fresh_type where dstatus = 0) type ON type.id = tinfo.classa LEFT JOIN (SELECT id,name as brand_name FROM t_fresh_brand where status = 0 ) brand ON brand.id = tinfo.brand_id LEFT JOIN (SELECT ifnull(MAX(t_sale_group.stock),0) as max_stock,codeId as g_codeId FROM t_sale_group GROUP BY codeId) group_table ON tinfo.codeId = group_table.g_codeId WHERE 1=1 and pname like '%${pname}%' and goodsName like '%${goodsName}%' and codeId=#{codeId}
and (((group_table.max_stock = 0 or group_table.max_stock is null) and tinfo.store <= 0) or pstatus = 2 )
and pstatus=#{pstatus}
and tel like '${tel}' and supplierId =#{supplierId} and supplierName like '%${supplierName}%' and (classa=#{classa} or type.fid = #{classa}) and discount >= #{minPrice} and discount <= #{maxPrice} AND province = #{province} AND city = #{city} AND choice = #{choice} ORDER BY sort DESC, sales DESC LIMIT ${(page-1)*limit},#{limit}

 select  from t_product_info 
 where codeId in

		#{item}

		 SELECT COUNT(1) FROM t_product_info
		 LEFT JOIN 
    		(SELECT ifnull(MAX(t_sale_group.stock),0) as max_stock,codeId as g_codeId FROM t_sale_group  GROUP BY  codeId) group_table
			ON t_product_info.codeId = group_table.g_codeId
		 where 1=1
			and pname like '%${pname}%'
			and goodsName like '%${goodsName}%'
			and codeId=#{codeId}


					and (group_table.max_stock = 0 or group_table.max_stock is null or  pstatus = 2 or t_product_info.store <= 0)

				and pstatus=#{pstatus}

			and tel like '${tel}'
			and supplierId =#{supplierId}
			and supplierName like '%${supplierName}%'
			and classa=#{classa}
			 and discount >= #{minPrice}
			 and discount <= #{maxPrice}
			AND province = #{province}
    		AND city = #{city}
    		AND choice = #{choice}

 		SELECT 

		FROM
			t_product_info 
		WHERE 1=1
			AND pname LIKE '%${pname}%'
			AND goodsName LIKE '%${goodsName}%'
			AND codeId = #{codeId}
			AND pstatus = #{pstatus}
			AND supplierId = #{supplierId}
			AND supplierName LIKE '%${supplierName}%'
			AND classa = #{classa}
			AND province = #{province}
    		AND city = #{city}
